
""" 
https://leetcode.com/problems/longest-palindrome/
In every palindrome has only one odd number, rest of are even number.
"""
def longest_palindrome(s):
    alpha = [0]*128
    n = len(s)
    for i in s:
        alpha[ord(i)] += 1
    odd_count = 0
    for i in alpha:
        if i%2 != 0:
            odd_count+=1
        
    return n-(odd_count-1) if odd_count > 1 else n


# s = "abccccdd"
# s ="aaaAaaaa"
s = ""
print(longest_palindrome(s))
